// Test generated by RoostGPT for test ReactStoryBook using AI Type Open AI and AI Model gpt-4

const { sleep } = require('./mockServiceWorker');

describe('sleep function', () => {
  let actualTime;

  beforeEach(() => {
    actualTime = Date.now();
  });

  test('should resolve after specified time', async () => {
    const timeMs = 1000;
    await sleep(timeMs);
    const timeAfterSleep = Date.now() - actualTime;
    expect(timeAfterSleep).toBeGreaterThanOrEqual(timeMs);
  });

  test('should resolve immediately if no time is specified', async () => {
    await sleep();
    const timeAfterSleep = Date.now() - actualTime;
    expect(timeAfterSleep).toBeLessThanOrEqual(5);
  });

  test('should throw an error if time is negative', async () => {
    const timeMs = -1000;
    await expect(sleep(timeMs)).rejects.toThrow();
  });
});
